CREATE VIEW [dbo].[Booking Record Sub]
	AS SELECT [dbo.Booking Records].Datum, [dbo.Booking Records].pkBooking, [dbo.Booking Run].pkBookingRun, [dbo.Booking Run].BookingRunYear, [dbo.Booking Run].BookingRunMonth, [dbo.Booking Run].BookingRunSeqNr, [dbo.Booking Records].SequenceNr, [dbo.Booking Records].Haben, [dbo.Booking Records].BU, [dbo.Booking Records].WKZ, [dbo.Booking Records].Umsatz, [dbo.Booking Records].[Umsatz-BW], [dbo.Booking Records].Belegfeld1, [dbo.Booking Records].Buchungstext, [dbo.Booking Records].Comment, [dbo.Booking Records].GegenkontoNr, Accounts.Name_german AS [Gegenkonto Name], [dbo.Booking Records].KontoNr, Accounts_1.Name_german AS [Konto Name], [dbo.Booking Records].KostenstelleGK AS [Kostenstelle GK ID], [Cost Center GK].[Cost Center Name] AS [Kostenstelle GK Name], [dbo.Booking Records].KostenstelleK AS [Kostenstelle K ID], [Cost Center K].[Cost Center Name] AS [Kostenstelle K Name], [dbo.Booking Records].Kostentraeger, [dbo.Booking Records].IsPlan
	FROM [dbo.Booking Run] INNER JOIN (((([dbo.Booking Records] INNER JOIN dbo.Accounts ON [dbo.Booking Records].GegenkontoNr = dbo.Accounts.pkAccount) INNER JOIN Accounts AS Accounts_1 ON [dbo.Booking Records].KontoNr = Accounts_1.pkAccount) LEFT JOIN [dbo.Cost Centers] AS [Cost Center GK] ON [dbo.Booking Records].[KostenstelleGK] = [Cost Center GK].pkCostCenterID) LEFT JOIN [dbo.Cost Centers] AS [Cost Center K] ON [dbo.Booking Records].[KostenstelleK] = [Cost Center K].pkCostCenterID) ON [dbo.Booking Run].pkBookingRun = [dbo.Booking Records].fkBookingRun
	WHERE ((([dbo.Booking Records].IsPlan)=False))
	ORDER BY [dbo.Booking Run].BookingRunYear, [dbo.Booking Run].BookingRunMonth, [dbo.Booking Run].BookingRunSeqNr, [dbo.Booking Records].SequenceNr;

